    }

    console.log(`Status update from ${keyData.stationId}:`, statusUpdate)

    return NextResponse.json({
      success: true,
      message: 'Status updated successfully',
      ...statusUpdate
    })

  } catch (error) {
    console.error('Error updating status:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

